mapper.select:
SELECT

FROM product_category
WHERE pid = #{pid}
AND status = #{status}
AND deleted = 0
ORDER BY sort ASC

mapper.insert:
INSERT INTO product_category (
  pid, name, description, pic_url, sort,
  status, create_time, deleted
) VALUES (
  #{pid}, #{name}, #{description}, #{picUrl}, #{sort},
  #{status}, #{createTime}, #{deleted}
)
