mapper.insert:
INSERT INTO mobile_code (
  id, mobile, code, today_index, used,
  used_uid, used_time, create_time
) VALUES (
  #{id}, #{mobile}, #{code}, #{todayIndex}, #{used},
  #{usedUserId}, #{usedTime}, #{createTime}
)

mapper.update:
UPDATE mobile_code
used = #{used}, used_uid = #{usedUserId}, used_time = #{usedTime},
WHERE id = #{id}
