mapper.insert:
INSERT INTO user_address (
    user_id, area_no, `name`, mobile, address,
    create_time, update_time, deleted
) VALUES (
  #{userId}, #{areaNo}, #{name}, #{mobile}, #{address},
  #{createTime}, #{updateTime}, #{deleted}
)

